#!/usr/bin/env python3
"""Test script for PUSH0 opcode support in bytecode analyzer."""

from src.bytecode_analyzer import BytecodeAnalyzer

# Bytecode containing PUSH0 (0x5f) opcode
bytecode = ""

print("Testing PUSH0 opcode support...")
print(f"Bytecode length: {len(bytecode)} characters")
print()

try:
    analyzer = BytecodeAnalyzer(bytecode)
    print(f"✓ Successfully parsed {len(analyzer.instructions)} instructions")
    print()
    
    # Check for PUSH0 instructions
    push0_count = sum(1 for i in analyzer.instructions if i['name'] == 'PUSH0')
    print(f"Found {push0_count} PUSH0 instructions")
    print()
    
    # Show first 10 instructions
    print("First 10 instructions:")
    for i, instr in enumerate(analyzer.instructions[:10]):
        operand = instr.get('operand', '')
        print(f"  {i+1}. {instr['name']:10s} {operand}")
    
    print()
    print("✓ Test PASSED! PUSH0 opcode is now supported.")
    
except Exception as e:
    print(f"✗ Test FAILED!")
    print(f"Error: {e}")
    import traceback
    traceback.print_exc()
